import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { IRegister } from 'src/app/models/IAuth';
import { IAuthUser } from 'src/app/models/IAuthResult';
import { AuthService } from 'src/app/shared/services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  registerForm : FormGroup;
  isCreate : string | null=""
  constructor(private _fb : FormBuilder, private _authService : AuthService, private _route : Router) {
    this.registerForm = this._fb.group({
      pseudo : [null, [Validators.required, Validators.minLength(3), Validators.maxLength(50)]],
      email : [null, [Validators.required, Validators.email]],
      lastname : [null, [Validators.required, Validators.maxLength(150)]],
      firstname : [null, [Validators.required, Validators.maxLength(150)]],
      password : [null, [Validators.required, Validators.pattern(/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[\W|_]).+$/)]]
    })
  }

  ngOnInit(): void {
      this._authService.$connectedUser.subscribe({
        next : (res : IAuthUser | undefined ) => {
          if(res) {
            this._route.navigateByUrl('/');
          }
        }
      })
  }

  register() : void {
    if(this.registerForm.valid){
      console.log(this.registerForm.value)
      let registerBody : IRegister = this.registerForm.value;
     this._authService.register(registerBody);
    this.isCreate=  sessionStorage.getItem('create');

    }
    else {
      this.registerForm.markAllAsTouched();
    }
  }
}
<div class="login flex justify-center items-center h-screen ">
  <form [formGroup]="registerForm"  (ngSubmit)="register()" class="w-1/4  bg-ciel flex justify-center items-center flex-col">
    <h3 class="mb-4 font-bold text-2xl">Créer un compte</h3>  
    <div class="mb-4 w-4/5 px-5">
          <input type="text" placeholder="Pseudo" formControlName="pseudo" class="w-full placeholder: p-1">
          <span *ngIf="registerForm.get('pseudo')?.touched && registerForm.get('pseudo')?.errors?.['required']">Champs requis</span>
      </div>
      <div class="mb-4 w-4/5 px-5">
          <input type="email" placeholder="Email" formControlName="email" class="w-full placeholder: p-1">
          <span *ngIf="registerForm.get('email')?.touched && registerForm.get('email')?.errors?.['required']">Champs requis</span>
      </div>
      <div class="mb-4 w-4/5 px-5">
          <input type="text" placeholder="Nom" formControlName="lastname" class="w-full placeholder: p-1">
          <span *ngIf="registerForm.get('lastname')?.touched && registerForm.get('lastname')?.errors?.['required']">Champs requis</span>
      </div>
      <div class="mb-4 w-4/5 px-5">
          <input type="text" placeholder="Prénom" formControlName="firstname" class="w-full placeholder: p-1">
          <span *ngIf="registerForm.get('firstname')?.touched && registerForm.get('firstname')?.errors?.['required']">Champs requis</span>
      </div>
      <div class="mb-4 w-4/5 px-5">
          <input type="password" placeholder="Mot de Passe" formControlName="password" class="w-full placeholder: p-1">
          <span *ngIf="registerForm.get('password')?.touched && registerForm.get('password')?.errors?.['required']">Champs requis</span>
          <span *ngIf="registerForm.get('password')?.dirty && registerForm.get('password')?.errors?.['pattern']">Regex Warning</span>
      </div>
      <div class="mb-2">
        <button type="submit" class="btn-log text-md font-bold">Créer</button>
    </div>
      <div *ngIf="isCreate ==='true'">
        <p>Compte à bien été créer</p>
      </div>
      
  </form>
</div>
