import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ILogin } from 'src/app/models/IAuth';
import { AuthService } from 'src/app/shared/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit{
  loginForm : FormGroup;

  constructor(private _fb : FormBuilder, private _authService : AuthService, private _route : Router) {
    this.loginForm = this._fb.group({
      identifier : [null, [Validators.required]],
      password : [null, [Validators.required]]
    })
  }

  ngOnInit(): void {
    sessionStorage.removeItem('create');
      this._authService.$connectedUser.subscribe({
        next : (res) => {
          if(res){
            this._route.navigateByUrl('/')
          }
        }
      })
  }

  login() : void {

    if(this.loginForm.valid){
      let loginBody : ILogin = this.loginForm.value;
      this._authService.login(loginBody);
    }

  }
}
<div class="login flex justify-center items-center h-screen ">
  <form [formGroup]="loginForm" (ngSubmit)="login()" class="w-1/4 h-1/4 bg-ciel flex justify-center items-center flex-col">
    <h3 class="mb-4 font-bold text-2xl">Connexion</h3>  
    <div class="mb-4 w-full px-5">
          <input type="text" placeholder="Login ou Email"  formControlName="identifier" class="w-2/5">
      </div>
      <div class="mb-4">
          <input type="password" placeholder="Mot de passe"  formControlName="password">
      </div>
      <div class="mb-2">
          <button type="submit" class="btn-log text-md font-bold">Se connecter</button>
      </div>

      <a routerLink="/gestion/register" class="mb-4"> <span class="font-bold" >Pas de compte ? </span> <span class="hover:text-brun">Cliquez ici pour en créer un</span>   </a>
  </form>
</div>
