import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { IEvent } from 'src/app/models/IEvenet';
import { EventService } from 'src/app/shared/services/event.service';

@Component({
  selector: 'app-create-event',
  templateUrl: './create-event.component.html',
  styleUrls: ['./create-event.component.scss']
})
export class CreateEventComponent {
  registerForm : FormGroup;
  isCreate : string | null=""
  constructor(private _fb : FormBuilder, private _eventService : EventService, private _route : Router) {
    this.registerForm = this._fb.group({
      name : [null, [Validators.required, Validators.minLength(3), Validators.maxLength(50)]],
      description : [null, [Validators.required]],
      startDate : [null, [Validators.required]],
      endDate : [null, [Validators.required]],
      maxGuest : [null, [Validators.required]]
    })
  }

  register() : void {
    if(this.registerForm.valid){
      console.log(this.registerForm.value)
      let registerBody : IEvent = this.registerForm.value;
     this._eventService.create(registerBody).subscribe({
      next : () => {
        this._route.navigateByUrl('/gestion/myevents');
      }
    });
    this.isCreate=  sessionStorage.getItem('create');

    }
    else {
      this.registerForm.markAllAsTouched();
    }
  }
}
<div class="login flex justify-center items-center h-screen ">
    <form [formGroup]="registerForm"  (ngSubmit)="register()" class="form">
        <h3 class="mb-4 font-bold text-2xl mt-4">Créer un event</h3>  
        <div class="mb-4 w-4/5 px-5">
            <input type="text" placeholder="Titre" formControlName="name" class="w-full placeholder: p-1">
        </div>

        <div class="mb-4 w-4/5 px-5">
            <textarea  placeholder="description" formControlName="description" class="w-full placeholder: p-1"></textarea>
        </div>

        <div class="mb-4 w-4/5 px-5">
            <input type="date" placeholder="Date Début" formControlName="startDate" class="w-full placeholder: p-1">
        </div>

        <div class="mb-4 w-4/5 px-5">
            <input type="date" placeholder="Date Fin" formControlName="endDate" class="w-full placeholder: p-1">
        </div>

        <div class="mb-4 w-4/5 px-5">
            <input type="number" placeholder="Nombre de place maximum" formControlName="maxGuest" class="w-full placeholder: p-1">
        </div>

        <div class="mb-2">
            <button type="submit" class="btn-log text-md font-bold mb-4">Créer</button>
        </div>
    </form>
</div>
